function jaf.internetlist(id)
         m="Internet list"
         for i=1,#jaf.inet_list do
             if jaf.inet_list[i] then
                m=m..","..jaf.inet_list[i].web
             end
         end
         menu(id,m)
end

function jaf.internet_publiccomment_save()
                  pc_data_sel=jaf.inet_list[2].subnet[1].info
                  pcw_data=""
                  local dir=jaf.loclua.."comment.txt"
                  file = io.output(io.open(dir,"w+"))
                  for i=1,#pc_data_sel do
                      if pc_data_sel[i]~=nil and pc_data_sel[i] then
                         pcw_data=pcw_data..""..pc_data_sel[i].."\n"
                      end
                  end
                  file:write(pcw_data)
                  file:close()
end

function jaf.internet_publiccomment_load()
                 local dir=jaf.loclua.."comment.txt"
                 local file = io.open(dir:format("comment", "r"))
                 getpc,getpcc={},0
                 if file then
	            for line in file:lines() do
                        getpcc=getpcc+1
                        getpc[getpcc]=line
                    end
                    file:close()
                    return getpc
                 else
                    jaf.internet_publiccomment_save()
                    file:close()
                    return getpc
                 end
end

function jaf.internet_publiccomment_input(ptxt)
                 jaf.inet_list[2].subnet[1].info=jaf.internet_publiccomment_load()
                 v=jaf.inet_list[2].subnet[1].info
                 if ptxt~=nil and ptxt then
                    getcp={ptxt}
                    for i=1,200 do
                        if v[i] then
                            table.insert(getcp,v[i])
                        end
                    end
                    jaf.inet_list[2].subnet[1].info=getcp
                    jaf.internet_publiccomment_save()
                 end
end

function jaf.netclearhudtxt2(id)
         parse('hudtxt2 '..id..' 27 "" 0 0')
         parse('hudtxt2 '..id..' 28 "" 0 0')
         parse('hudtxt2 '..id..' 29 "" 0 0')
         parse('hudtxt2 '..id..' 30 "" 0 0')
         parse('hudtxt2 '..id..' 31 "" 0 0')
         parse('hudtxt2 '..id..' 32 "" 0 0')
         parse('hudtxt2 '..id..' 33 "" 0 0')
         parse('hudtxt2 '..id..' 34 "" 0 0')
         parse('hudtxt2 '..id..' 35 "" 0 0')
         parse('hudtxt2 '..id..' 36 "" 0 0')
         if jaf.joninet[id]==nil and jaf.getnetimage[id]~=nil and tonumber(#jaf.getnetimage[id]) then
                      jaf.freenetimage(id,#jaf.getnetimage[id])
                      jaf.getnetimage[id]=nil
         end
end

function jaf.inet_search(no,text)
         getsb,getsbc={},0
         if jaf.inet_list[no] then
           for i=1,#jaf.inet_list[no].subnet do
              v=jaf.inet_list[no].subnet[i]
              if string.lower(v.title):find(string.lower(text)) then 
                  getsbc=getsbc+1
                  if type(v.info)~="table" then
                     getsb[getsbc]=i
                  elseif type(v.info)=="table" then
                     getsb[getsbc]={i,1}
                  end
              elseif type(v.info)=="table" then
                  for ii=1,#v.info do
                      if string.lower(v.info[ii]):find(string.lower(text)) then
                         getsbc=getsbc+1
                         getsb[getsbc]={i,ii}
                      end
                  end
               elseif type(v.info)~="table" and string.lower(v.info):find(string.lower(text)) then
                  getsbc=getsbc+1
                  getsb[getsbc]=i
               end
           end
        end
        if getsbc>0 then return getsb else return false end
end


function jaf.inetfind_list(id,page)
eck=false
if jaf.joninet[id]~=nil then eck=jaf.inet_search(jaf.getnet[id],jaf.getnetsearch[id]) end
if eck then
local page=page or 1
local pages=math.ceil(#eck/7)
local m='Net search result | Page '..page
    for i=7*page-6, 7*page do
         if eck[i] then
                if type(eck[i])=="table" then
                   v=jaf.inet_list[jaf.getnet[id]].subnet[eck[i][1]]
                   m=m..','..v.sub..'|In list No. '..eck[i][2]
                else
                   v=jaf.inet_list[jaf.getnet[id]].subnet[eck[i]]
                   m=m..','..v.sub
                end
          else
                m=m..','
          end
    end
         langpage=jaf.sv_lang(jaf.lang[id],8)
         if page >= pages then 
              m = m ..','..jaf.sv_lang(jaf.lang[id],5)..'|'..langpage..' '..(page+1)..''
         else
              m = m ..','..jaf.sv_lang(jaf.lang[id],5)..'|'..langpage..' '..(page+1)..''
         end
         if page == 1 then 
              m = m ..',('..jaf.sv_lang(jaf.lang[id],7)..'|X)' 
         else
              m = m ..','..jaf.sv_lang(jaf.lang[id],6)..'|'..langpage..' '..(page-1)..'' 
         end
              menu(id,m)
else
    jaf.svj_msg2(id,"[System] "..merah..""..jaf.sv_lang(jaf.lang[id],129,jaf.getnetsearch[id]))
end
end

function jaf.freenetimage(id,count)
      if tonumber(count) and tonumber(id) and id>0 then
         for i=1,count do
             if jaf.netimage[id][i]~=nil then
                freeimage(jaf.netimage[id][i])
                jaf.netimage[id][i]=nil
             end
         end
      end
end

function jaf.link_CMD_refresh()
         for i=1,#jaf.cmd do
                rank=hijau.."[User]"
                getsub=""
                if jaf.cmd[i].admin>0 then
                     rpt=jaf.cmd[i].admin
                     if rpt>#jaf.admintag then rpt=#jaf.admintag end
                     rank=jaf.admintag[rpt][1].."["..jaf.admintag[rpt][2].."]"
                end
                if jaf.cmd[i].sub~=nil then getsub=jaf.cmd[i].sub end
                jaf.inet_list[2].subnet[5].info[i]=rank.." "..emas..""..jaf.cmd[i].cmd.." "..getsub
         end
end